import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import API from "../services/api";

// Dummy fallback recipes
const dummyRecipes = [
  {
    id: 1,
    title: "Spicy Ugali Delight",
    description: "A Kenyan twist with some heat!",
    image: "/src/assets/images/a1034bea2b87f98a8e7f102470a5bfd0.jpg",
    serves: 2,
    time: "30 mins",
    difficulty: "Easy",
    ingredients: ["2 cups maize flour", "1 tsp chili flakes", "Water", "Salt"],
    steps: ["Boil water", "Add flour and stir", "Add chili", "Serve hot"],
    rating: 4.5,
    comments: [
      { username: "ChefKen", text: "Spicy and awesome!" },
      { username: "MamaNyumbani", text: "Loved it with sukuma!" },
    ],
  },
  {
    id: 2,
    title: "Matoke Mash",
    description: "Banana bliss in every bite.",
    image: "/src/assets/images/download (1).jpeg",
    serves: 4,
    time: "45 mins",
    difficulty: "Medium",
    ingredients: ["5 bananas", "1 onion", "Oil", "Salt"],
    steps: ["Peel and chop", "Boil bananas", "Fry onions", "Mash together"],
    rating: 4.2,
    comments: [{ username: "Foodie254", text: "Bananas never tasted better!" }],
  },
];

const RecipeDetail = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);

  useEffect(() => {
    // Try API, fallback to dummy if fails
    API.get(`/recipes/${id}`)
      .then((res) => setRecipe(res.data))
      .catch((err) => {
        console.warn("API failed. Falling back to dummy data.");
        const fallback = dummyRecipes.find((r) => r.id === parseInt(id));
        if (fallback) setRecipe(fallback);
      });
  }, [id]);

  if (!recipe) return <div className="loading-message">Loading...</div>;

  return (
    <div className="recipe-detail-container">
      <h1 className="recipe-title">{recipe.title}</h1>

      <img
        src={recipe.image || "https://via.placeholder.com/800x400"}
        alt={recipe.title}
        className="recipe-image"
      />

      <p className="recipe-description">{recipe.description}</p>

      <div className="recipe-meta">
        <p><strong>Serves:</strong> {recipe.serves || "N/A"}</p>
        <p><strong>Time:</strong> {recipe.time || "N/A"}</p>
        <p><strong>Difficulty:</strong> {recipe.difficulty || "N/A"}</p>
      </div>

      <h3 className="section-heading">Ingredients</h3>
      <ul className="ingredients-list">
        {recipe.ingredients?.map((ing, index) => (
          <li key={index}>{ing}</li>
        ))}
      </ul>

      <h3 className="section-heading">Instructions</h3>
      <ol className="instructions-list">
        {recipe.steps?.map((step, index) => (
          <li key={index}>{step}</li>
        ))}
      </ol>

      <div className="rating-section">
        <h3 className="section-heading">Rating</h3>
        <p className="rating-value">{recipe.rating || "No ratings yet"}</p>
      </div>

      <div className="comments-section">
        <h3 className="section-heading">Comments</h3>
        {recipe.comments?.length > 0 ? (
          recipe.comments.map((comment, index) => (
            <div key={index} className="comment">
              <p>
                <strong>{comment.username || "User"}:</strong>{" "}
                {comment.text || comment.comment}
              </p>
            </div>
          ))
        ) : (
          <p>No comments yet.</p>
        )}
      </div>

      <div className="share-section">
        <button
          onClick={() => navigator.clipboard.writeText(window.location.href)}
          className="share-button"
        >
        </button>
      </div>
    </div>
  );
};

export default RecipeDetail;
