import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import API from ".services/api";

const RecipeDetail = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);

  useEffect(() => {
    API.get(`/recipes/${id}`)
      .then((res) => setRecipe(res.data))
      .catch((err) => console.error(err));
  }, [id]);

  if (!recipe) return <div className="loading-message">Loading...</div>;

  return (
    <div className="recipe-detail-container" style={{ padding: "2rem" }}>
      <h1 className="recipe-title" style={{ fontSize: "2rem", fontWeight: "bold", marginBottom: "1rem" }}>
        {recipe.title}
      </h1>

      <p className="serves-count" style={{ marginBottom: "1rem", fontStyle: "italic" }}>
        Serves: {recipe.serves}
      </p>

      <h3 className="section-heading">Ingredients</h3>
      <ul className="ingredients-list">
        {recipe.ingredients?.map((ing, index) => (
          <li key={index}>{ing}</li>
        ))}
      </ul>

      <h3 className="section-heading">Instructions</h3>
      <ol className="instructions-list">
        {recipe.steps?.map((step, index) => (
          <li key={index}>{step}</li>
        ))}
      </ol>

      <div className="rating-section">
        <h3 className="section-heading">Rating</h3>
        <p className="rating-value">{recipe.rating || "No ratings yet"}</p>
      </div>

      <div className="comments-section">
        <h3 className="section-heading">Comments</h3>
        {recipe.comments?.length ? (
          recipe.comments.map((comment, index) => (
            <div key={index} className="comment-box" style={{ backgroundColor: "#fdbb89", padding: "0.75rem", marginBottom: "0.5rem", borderRadius: "0.5rem" }}>
              <p className="comment-text" style={{ fontWeight: "bold" }}>{comment.text}</p>
              <p className="comment-user" style={{ fontSize: "0.9rem", color: "#555" }}>By {comment.user}</p>
            </div>
          ))
        ) : (
          <p className="no-comments">No comments yet</p>
        )}
      </div>

      <div className="share-section" style={{ marginTop: "1rem" }}>
        <button
          onClick={() => navigator.clipboard.writeText(window.location.href)}
          className="share-button"
          style={{
            backgroundColor: "#f47e3b",
            color: "#fff",
            padding: "0.5rem 1rem",
            border: "none",
            borderRadius: "0.5rem",
            cursor: "pointer",
          }}
        >
        </button>
      </div>
    </div>
  );
};

export default RecipeDetail;
