import React, { useEffect, useState } from 'react';
import { uploadImageToCloudinary } from '../services/cloudinary';
import CommentSection from '../components/CommentBox';
import API from '../services/api';

const Profile = () => {
  const [userData, setUserData] = useState({
    name: '',
    email: '',
    image: '',
    password: '',
  });

  const [userRecipes, setUserRecipes] = useState([]);
  const [bookmarkedRecipes, setBookmarkedRecipes] = useState([]);
  const [userComments, setUserComments] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await API.get('/users/profile');
        setUserData(res.data);
        localStorage.setItem('user_profile', JSON.stringify(res.data));
      } catch (err) {
        console.error('Error fetching profile:', err);
      }
    };

    const fetchUserExtras = async () => {
      try {
        const [recipesRes, bookmarksRes, commentsRes] = await Promise.all([
          API.get('/recipes/my-recipes'), // Adjust endpoint if needed
          API.get('/bookmarks'),
          API.get('/comments/my-comments'),
        ]);

        setUserRecipes(recipesRes.data || []);
        setBookmarkedRecipes(bookmarksRes.data || []);
        setUserComments(commentsRes.data || []);
      } catch (err) {
        console.error('Error loading extras:', err);
      }
    };

    fetchProfile();
    fetchUserExtras();
  }, []);

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    try {
      setLoading(true);
      const imageUrl = await uploadImageToCloudinary(file);
      setUserData((prev) => ({ ...prev, image: imageUrl }));
      setLoading(false);
    } catch (err) {
      console.error('Cloudinary upload failed:', err);
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const payload = {
        name: userData.name,
        email: userData.email,
        image: userData.image,
      };

      if (userData.password) {
        payload.password = userData.password;
      }

      const res = await API.put('/users/profile', payload);
      alert('Profile updated successfully!');
      localStorage.setItem('user_profile', JSON.stringify(res.data));
      setUserData((prev) => ({ ...prev, password: '' }));
    } catch (err) {
      console.error('Error updating profile:', err);
      alert('Failed to update profile.');
    }
  };

  return (
    <div className="min-h-screen p-8 bg-[#fdbb89]">
      <h1 className="text-3xl font-bold mb-8 text-center">My Profile</h1>

      {/* PROFILE UPDATE FORM */}
      <form onSubmit={handleSubmit} className="space-y-6 max-w-xl mx-auto bg-white p-6 rounded-lg shadow-md">
        <div className="flex flex-col items-center">
          {userData.image && (
            <img src={userData.image} alt="Profile" className="w-32 h-32 rounded-full mb-4 object-cover" />
          )}
          <input type="file" onChange={handleImageChange} />
        </div>

        <div>
          <label>Name:</label>
          <input
            name="name"
            value={userData.name}
            onChange={handleInputChange}
            className="w-full p-2 border rounded"
            required
          />
        </div>

        <div>
          <label>Email:</label>
          <input
            name="email"
            value={userData.email}
            onChange={handleInputChange}
            className="w-full p-2 border rounded"
            required
          />
        </div>

        <div>
          <label>New Password:</label>
          <input
            name="password"
            type="password"
            value={userData.password}
            onChange={handleInputChange}
            className="w-full p-2 border rounded"
            placeholder="Leave blank to keep current password"
          />
        </div>

        <button
          type="submit"
          className="bg-[#f47e3b] text-white px-4 py-2 rounded hover:bg-[#fb9751]"
          disabled={loading}
        >
          {loading ? 'Updating...' : 'Update Profile'}
        </button>
      </form>

      {/* LOGOUT AND DELETE */}
      <div className="mt-10 text-center space-x-4">
        <button
          onClick={() => {
            localStorage.clear();
            window.location.href = '/login';
          }}
          className="bg-black text-white px-4 py-2 rounded hover:bg-gray-800"
        >
          Logout
        </button>

        <button
          onClick={async () => {
            const confirmDelete = window.confirm('Are you sure you want to delete your profile? This cannot be undone.');
            if (!confirmDelete) return;

            try {
              await API.delete('/users/profile');
              localStorage.clear();
              window.location.href = '/signup';
            } catch (err) {
              console.error('Delete failed:', err);
              alert('Failed to delete your profile.');
            }
          }}
          className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
        >
          Delete Profile
        </button>
      </div>

      {/* RECIPES SECTION */}
      <section className="mt-12">
        <h2 className="text-2xl font-semibold mb-4">My Recipes</h2>
        <div className="grid grid-cols-2 gap-4">
          {userRecipes.map((recipe) => (
            <div key={recipe._id} className="border p-3 rounded bg-white shadow">
              <img src={recipe.image} alt={recipe.title} className="w-full h-40 object-cover rounded mb-2" />
              <h3 className="text-lg font-bold">{recipe.title}</h3>
            </div>
          ))}
        </div>
      </section>

      {/* BOOKMARKS SECTION */}
      <section className="mt-12">
        <h2 className="text-2xl font-semibold mb-4">Bookmarked Recipes</h2>
        <div className="grid grid-cols-2 gap-4">
          {bookmarkedRecipes.map((recipe) => (
            <div key={recipe._id} className="border p-3 rounded bg-white shadow">
              <img src={recipe.image} alt={recipe.title} className="w-full h-40 object-cover rounded mb-2" />
              <h3 className="text-lg font-bold">{recipe.title}</h3>
            </div>
          ))}
        </div>
      </section>

      {/* COMMENTS SECTION */}
      <section className="mt-12">
        <h2 className="text-2xl font-semibold mb-4">My Comments</h2>
        <ul className="space-y-4">
          {userComments.map((comment) => (
            <li key={comment._id} className="bg-white p-4 rounded shadow">
              <p className="text-sm text-gray-600">On: {comment.recipeTitle}</p>
              <p className="text-black">{comment.text}</p>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
};

export default Profile;
