import { Routes, Route, Navigate } from 'react-router-dom';
import { useSelector } from 'react-redux';

import Signup from '../pages/Signup';
import Login from '../pages/Login';
import Home from '../pages/Home';
import Profile from '../pages/Profile';
import RecipeDetail from '../pages/RecipeDetail';
import Bookmarks from '../pages/Bookmarks';
import CreateRecipe from '../pages/CreateRecipe';
import GroupRecipe from '../pages/GroupRecipe';

const AppRoutes = () => {
  const { isAuthenticated, loading } = useSelector((state) => state.auth);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen text-orange-600 text-xl">
        Loading...
      </div>
    );
  }

  return (
    <Routes> //Public Routes
      <Route path="/" element={<Home />} />
      <Route
        path="/signup"
        element={isAuthenticated ? <Navigate to="/" replace /> : <Signup />}
      />
      <Route
        path="/login"
        element={isAuthenticated ? <Navigate to="/" replace /> : <Login />}
      /> 

//Private Routes
      {isAuthenticated ? (
        <>
          <Route path="/profile" element={<Profile />} />
          <Route path="/recipes/:id" element={<RecipeDetail />} />
          <Route path="/bookmarks" element={<Bookmarks />} />
          <Route path="/create" element={<CreateRecipe />} />
          <Route path="/group" element={<GroupRecipe />} />
        </>
      ) : (
        <>
          <Route path="/profile" element={<Navigate to="/login" replace />} />
          <Route path="/recipes/:id" element={<Navigate to="/login" replace />} />
          <Route path="/bookmarks" element={<Navigate to="/login" replace />} />
          <Route path="/create" element={<Navigate to="/login" replace />} />
          <Route path="/group" element={<Navigate to="/login" replace />} />
        </>
      )}

      <Route path="*" element={<Navigate to="/" replace />} />
    </Routes>
  );
};

export default AppRoutes;
