import React, { useEffect, useState } from 'react';
import { uploadImageToCloudinary } from '../services/cloudinary';
import API from '../services/api';
import { motion } from 'framer-motion';

const Profile = () => {
  const [userData, setUserData] = useState({
    name: '',
    email: '',
    image: '',
    password: '',
  });
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await API.get('/users/profile');
        setUserData(res.data);
        localStorage.setItem('user_profile', JSON.stringify(res.data));
      } catch (err) {
        console.error('Error fetching profile:', err);
      }
    };
    fetchProfile();
  }, []);

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    try {
      setLoading(true);
      const imageUrl = await uploadImageToCloudinary(file);
      setUserData((prev) => ({ ...prev, image: imageUrl }));
      setLoading(false);
    } catch (err) {
      console.error('Cloudinary upload failed:', err);
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const payload = {
        name: userData.name,
        email: userData.email,
        image: userData.image,
      };
      if (userData.password) {
        payload.password = userData.password;
      }

      const res = await API.put('/users/profile', payload);
      alert('Profile updated successfully!');
      localStorage.setItem('user_profile', JSON.stringify(res.data));
      setUserData((prev) => ({ ...prev, password: '' }));
    } catch (err) {
      console.error('Error updating profile:', err);
      alert('Failed to update profile.');
    }
  };

  return (
    <motion.div
      className="min-h-screen flex items-center justify-center bg-[#fdbb89] p-6"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.6 }}
    >
      <motion.form
        onSubmit={handleSubmit}
        className="bg-white shadow-lg rounded-xl p-8 w-full max-w-lg space-y-6"
        initial={{ y: 50 }}
        animate={{ y: 0 }}
        transition={{ duration: 0.6, delay: 0.2 }}
      >
        <h1 className="text-3xl font-bold text-[#f47e3b] text-center">My Profile</h1>

        <div className="text-center">
          {userData.image && (
            <img
              src={userData.image}
              alt="Profile"
              className="w-32 h-32 rounded-full mx-auto object-cover mb-4 border-4 border-[#fb9751]"
            />
          )}
          <input type="file" onChange={handleImageChange} className="text-sm" />
        </div>

        <div>
          <label className="block font-semibold mb-1">Name:</label>
          <input
            name="name"
            value={userData.name}
            onChange={handleInputChange}
            className="w-full p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-[#f47e3b]"
            required
          />
        </div>

        <div>
          <label className="block font-semibold mb-1">Email:</label>
          <input
            name="email"
            value={userData.email}
            onChange={handleInputChange}
            className="w-full p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-[#f47e3b]"
            required
          />
        </div>

        <div>
          <label className="block font-semibold mb-1">New Password:</label>
          <input
            name="password"
            type="password"
            value={userData.password}
            onChange={handleInputChange}
            className="w-full p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-[#f47e3b]"
            placeholder="Leave blank to keep current password"
          />
        </div>

        <button
          type="submit"
          className="w-full bg-[#f47e3b] text-white font-bold py-2 px-4 rounded hover:bg-[#fb9751] transition-all"
          disabled={loading}
        >
          {loading ? 'Updating...' : 'Update Profile'}
        </button>

        <div className="flex justify-between items-center pt-4">
          <button
            type="button"
            onClick={() => {
              localStorage.clear();
              window.location.href = '/login';
            }}
            className="bg-black text-white px-4 py-2 rounded hover:bg-gray-800"
          >
            Logout
          </button>

          <button
            type="button"
            onClick={async () => {
              const confirmDelete = window.confirm('Are you sure you want to delete your profile? This cannot be undone.');
              if (!confirmDelete) return;
              try {
                await API.delete('/users/profile');
                localStorage.clear();
                window.location.href = '/signup';
              } catch (err) {
                console.error('Delete failed:', err);
                alert('Failed to delete your profile.');
              }
            }}
            className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
          >
            Delete
          </button>
        </div>
      </motion.form>
    </motion.div>
  );
};

export default Profile;
