import React from 'react';
import recipe1 from '../assets/images/';
import recipe2 from '../assets/images/recipe2.jpg';
import recipe3 from '../assets/images/recipe3.jpg';
import recipe4 from '../assets/images/recipe4.jpg';
import '../styles/global.css'; // Make sure global styles are applied

const featuredImages = [recipe1, recipe2, recipe3, recipe4];

const Home = () => {
  return (
    <div className="home-wrapper">
      <div className="hero-container">
        <h1>Find and share simple recipes</h1>
        <p>Discover easy-to-make recipes and contribute your own contributions</p>
        <div className="search-bar">
          <input type="text" placeholder="Search for recipes..." />
        </div>
      </div>

      <div className="featured-section">
        <h2>Featured Recipes</h2>
        <div className="recipe-grid">
          {featuredImages.map((img, index) => (
            <div key={index} className="recipe-card">
              <img src={img} alt={`Recipe ${index + 1}`} />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
